const log = require('lambda-log');
const request = require('sync-request');

class Http {
  constructor(url) {
    this.url = url;
    this.result;
  };

  get() {
   try {
      log.debug("URL:" + this.url);
      var res1 = request('GET', this.url);
      var jsonDoc =  JSON.parse(res1.getBody('utf8'));
      return jsonDoc;
    } catch (e) {
      console.log(e);
    }
  }
}

module.exports = Http;
