const log = require('lambda-log');
const AWS = require('aws-sdk');

var s3 = new AWS.S3();

class S3 {
  constructor(bucketName, fileName, content) {
    this.bucketName = bucketName;
    this.fileName = fileName;
    this.content = content;
  };

  async write() {
    const params = {
      "Bucket": this.bucketName,
      "Key": this.fileName,
      "Body": this.content,
      "ContentType": "application/json"
    };
    log.debug("BucketName " + this.bucketName);
    log.debug("Key " + this.fileName);
    log.debug("Body " + this.content);

    try {
      await s3.putObject(params).promise();
      log.debug("Uploaded file...");
      return;
    }
    catch (error) {
      console.log(error);
      throw error;
    }
  }
}

class TestS3 {
  test() {
    console.log("Testing S3");
    var s3 = new S3("storage-bucket-123", "test/test8.json", JSON.stringify('{ "Name": "Joe"}'), "us-east-1");
    s3.write();
  }
}

